	const handleChangeMode = () => {
		renderDarkmodeText();

		if (mode === 'light') {
			setMode('dark');
			console.log(paragraph.value);
			// setParagraph('Tienes activado el dark mode');
		} else {
			setMode('light');
			// setParagraph('');
		}
	};

	const renderDarkmodeText = () => {
		if (paragraph === 'undefined') {
			setParagraph('Tienes activado el dark mode');
		} else {
			setParagraph('');
		}
	};

	return (
		<div className={mode}>
