import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [paragraph, setParagraph] = useState('Eres un robot sin paladar');
	const [cebolla, setCebolla] = useState(false);
	const [huevos, setHuevos] = useState(false);
	const [patatas, setPatatas] = useState(false);

	const handleCebolla = (event) => {
		setCebolla(event.target.checked);
	};

	const handleHuevos = (event) => {
		setHuevos(event.target.checked);
	};

	console.log(cebolla);

	const handleTortilla = (event) => {
		let checkedIngredient = event.target.id;

		console.log(checkedIngredient);

		if (event.currentTarget.checked === false) {
			setParagraph('Eres un robot sin paladar');
		} else if (checkedIngredient === cebolla) {
			setParagraph('Eres una persona concebollista');
		} else {
			setParagraph('Eres un robot sin paladar');
		}
	};
	return (
		<div>
			<h1>Selecciona los ingredientes de la tortilla de patata</h1>

			<form className="formContainer" action="">
				<label htmlFor="macarrones">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="noRecipe"
						name="ing"
						id="macarrones"
					/>
					Macarrones
				</label>

				<label htmlFor="patatas">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="patatas"
					/>
					Patatas
				</label>
				<label htmlFor="nueces">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="noRecipe"
						name="ing"
						id="nueces"
					/>
					Nueces
				</label>
				<label htmlFor="huevos">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="huevos"
					/>
					Huevos
				</label>
				<label htmlFor="cebolla">
					<input
						onChange={handleCebolla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="cebolla"
					/>
					Cebolla
				</label>
				<label htmlFor="cerveza">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="noRecipe"
						name="ing"
						id="cerveza"
					/>
					Cerveza
				</label>
			</form>

			<p>{paragraph}</p>
		</div>
	);
}

export default App;
