import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [paragraph, setParagraph] = useState('Eres un robot sin paladar');
	const [cebolla, setCebolla] = useState(false);
	const [huevos, setHuevos] = useState('');
	const [patatas, setPatatas] = useState('');

	const handleTortilla = (event) => {
		if ((event.target.id = 'cebolla')) {
			setCebolla(event.target.checked);
			console.log(event.target.checked);
		}
	};

	// if (cebolla === true && huevos === true && patatas === true) {
	// 	setParagraph('Eres una persona concebollista');
	// } else {
	// 	setParagraph('Eres un robot sin paladar');
	// }
	return (
		<div>
			<h1>Selecciona los ingredientes de la tortilla de patata</h1>

			<form className="formContainer" action="">
				<label htmlFor="macarrones">
					<input type="checkbox" value="noRecipe" name="ing" id="macarrones" />
					Macarrones
				</label>

				<label htmlFor="patatas">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="patatas"
					/>
					Patatas
				</label>
				<label htmlFor="nueces">
					<input type="checkbox" value="noRecipe" name="ing" id="nueces" />
					Nueces
				</label>
				<label htmlFor="huevos">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="huevos"
					/>
					Huevos
				</label>
				<label htmlFor="cebolla">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="recipe"
						name="ing"
						id="cebolla"
					/>
					Cebolla
				</label>
				<label htmlFor="cerveza">
					<input type="checkbox" value="noRecipe" name="ing" id="cerveza" />
					Cerveza
				</label>
			</form>

			<p>{paragraph}</p>
		</div>
	);
}

export default App;
