import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [paragraph, setParagraph] = useState('');

	const handleTortilla = (event) => {
		let ingredient = event.currentTarget.id;
		let selected = event.currentTarget.value;
		let checked = event.currentTarget.checked;

		console.log(ingredient);
		console.log(selected);
		console.log(checked);

		if (checked === false) {
			setParagraph('Eres un robot sin paladar');
		} else if (selected === 'true') {
			setParagraph('Eres una persona concebollista');
		}
		// if (checked === true) {
		// 	if (
		// 		ingredient === 'macarrones' ||
		// 		ingredient === 'nueces' ||
		// 		ingredient === 'cerveza'
		// 	) {
		// 		if (selected === 'false') {
		// 			setParagraph('Eres un robot sin paladar');
		// 		}
		// 	} else {
		// 		setParagraph('Eres una persona concebollista');
		// 	}
		// }
	};
	return (
		<div>
			<h1>Selecciona los ingredientes de la tortilla de patata</h1>

			<form className="formContainer" action="">
				<label htmlFor="macarrones">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="false"
						name="macarrones"
						id="macarrones"
					/>
					Macarrones
				</label>

				<label htmlFor="patatas">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="patatas"
						id="patatas"
					/>
					Patatas
				</label>
				<label htmlFor="nueces">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="false"
						name="nueces"
						id="nueces"
					/>
					Nueces
				</label>
				<label htmlFor="huevos">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="huevos"
						id="huevos"
					/>
					Huevos
				</label>
				<label htmlFor="cebolla">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="cebolla"
						id="cebolla"
					/>
					Cebolla
				</label>
				<label htmlFor="cerveza">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="false"
						name="cerveza"
						id="cerveza"
					/>
					Cerveza
				</label>
			</form>

			<p>{paragraph}</p>
		</div>
	);
}

export default App;
