import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [paragraph, setParagraph] = useState('');

	const handleTortilla = (event) => {
		let ingredient = event.currentTarget.id;
		let selected = event.currentTarget.checked;
		const recipe = ['cebolla', 'huevo', 'patatas'];
		let array = [];
		array.push(event.currentTarget.id);
		console.log(recipe);
		console.log(array);

		// if (selected === true) {
		// 	if (
		// 		ingredient === 'patatas' ||
		// 		ingredient === 'huevos' ||
		// 		ingredient === 'cebolla'
		// 	) {
		// 		setParagraph('Eres una persona concebollista');
		// 	}
		// } else {
		// 	setParagraph('Eres un robot sin paladar');
		// }
	};
	return (
		<div>
			<h1>Selecciona los ingredientes de la tortilla de patata</h1>

			<form className="formContainer" action="">
				<label htmlFor="macarrones">
					<input
						type="checkbox"
						value="false"
						name="macarrones"
						id="macarrones"
					/>
					Macarrones
				</label>

				<label htmlFor="patatas">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="patatas"
						id="patatas"
					/>
					Patatas
				</label>
				<label htmlFor="nueces">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="false"
						name="nueces"
						id="nueces"
					/>
					Nueces
				</label>
				<label htmlFor="huevos">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="huevos"
						id="huevos"
					/>
					Huevos
				</label>
				<label htmlFor="cebolla">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="true"
						name="cebolla"
						id="cebolla"
					/>
					Cebolla
				</label>
				<label htmlFor="cerveza">
					<input
						onChange={handleTortilla}
						type="checkbox"
						value="false"
						name="cerveza"
						id="cerveza"
					/>
					Cerveza
				</label>
			</form>

			<p>{paragraph}</p>
		</div>
	);
}

export default App;
