import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [numberA, setNumberA] = useState(0);
	const [numberB, setNumberB] = useState(0);
	const [operator, setOperator] = useState('sum');

	const handleChangeOperator = (ev) => {
		const Operator = ev.currentTarget.value;
	};

	const handleNumberA = (ev) => {
		const newValue = ev.currentTarget.value;
		setNumberA(parseInt(newValue));
	};

	const handleNumberB = (ev) => {
		const newValue = ev.currentTarget.value;
		setNumberB(parseInt(newValue));
	};

	let total;

	if (operator === 'sum') {
		total = numberA + numberB;
	} else {
		total = numberA - numberB;
	}

	return (
		<div>
			<h1>La calculadora:</h1>
			<form>
				<select
					name="operator"
					id="operator"
					value={operator}
					onChange={handleChangeOperator}
				>
					<option value="sum">Sumar</option>
					<option value="subs">Restar</option>
				</select>
				<label>
					Escribe un número:
					<input
						type="number"
						name="name"
						onChange={handleNumberA}
						value={numberA}
					/>
				</label>
				<label>
					Escribe otro número:
					<input
						type="number"
						name="email"
						onChange={handleNumberB}
						value={numberB}
					/>
				</label>
			</form>
			<p>
				La suma de <strong>{numberA}</strong> y <strong>{numberB}</strong> es
				<strong> {total}</strong>.
			</p>
		</div>
	);
}

export default App;
