import { useState } from 'react';
import '../styles/App.scss';

function App() {
	const [tasks, setTasks] = useState([
		{ task: 'Comprar harina, jam칩n y pan rallado', completed: true },
		{ task: 'Hacer croquetas ricas', completed: true },
		{ task: 'Ir a la puerta de un gimnasio', completed: false },
		{
			task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio',
			completed: false,
		},
	]);

	const handleClicked = (event) => {
		const clickedTaskId = event.currentTarget.id;

		const foundTask = tasks.find((item) => item.id === clickedTaskId);

		console.log(tasks);
		// foundTask.completed = !foundTask.completed;
		console.log(foundTask);

		setTasks([...tasks]);
	};

	const renderTasks = () => {
		for (const item of tasks) {
			item.id = tasks.indexOf(item);
		}
		setTasks({ ...tasks });
		return tasks.map((task, index) => {
			if (task.completed === true) {
				return (
					<li
						onClick={handleClicked}
						id={index}
						className="completed"
						key={index}
					>
						{task.task}
					</li>
				);
			} else {
				return (
					<li onClick={handleClicked} id={index} key={index}>
						{task.task}
					</li>
				);
			}
		});
	};
	return (
		<div>
			<h1>Lista de tareas</h1>
			<ul>{renderTasks()}</ul>
		</div>
	);
}

export default App;
